import React, {Component} from 'react';
import './App.css';
import * as artifact from './contracts/NoSecret'


class App extends Component {

    constructor(props) {
        super(props);

        window.tronWeb.setDefaultBlock('latest');

        this.contract = null;
        this.state = {
            address: null,
            balance: null,
            contract: null,
            tokenBalance: null,
            result_0: null,
            result_1: null,
            result_2: null
        }
    }

    async componentDidMount() {

        let tronWeb = window.tronWeb;
        console.log('address', tronWeb.defaultAddress.base58)
        this.setState({address: tronWeb.defaultAddress.base58});
        let address = tronWeb.address.fromHex(artifact.networks['*'].address);
        console.log(artifact.abi, artifact.networks['*'].address, address)
        this.contract = tronWeb.contract(artifact.abi, address);
        console.log(this.contract)
        //await this.getCircleKey();
        await this.createCircle();
        // await this.refreshTokenBalance();
    }

//首次登录，注册用户
    //判断是否首次登录
    //true 表示已经注册，false表示第一次登录，需要注册
    checkUser = async () => {
        let result_0 = await this.contract.checkUser().call()
        console.log(result_0)
        this.setState({result_0:JSON.stringify(result_0)})
    };

    getUser = async () => {
        let result_0 = await this.contract.getUser().call()
        console.log(result_0)
        this.setState({result_0:JSON.stringify(result_0)})
    };

    getCircleKey = async () => {
        let result_0 = await this.contract.getCircleKey().call()
        console.log(result_0)
        this.setState({result_0:JSON.stringify(result_0)})
    };

    //创建用户，参数是一个数组[],里面的值是circle_key
    createUser = async () => {
        let circle_join = [1,2];
        let result_0 = await this.contract.createUser(circle_join).send()
        console.log(result_0)
        this.setState({result_0:JSON.stringify(result_0)})
    };

    //创建圈子，参数是一个整数，是加入这个圈子需要收取的费用，0则表示免费
    createCircle = async () => {
        let fee = 0;
        //await tronWeb.transactionBuilder.sendTrx("TXPHCzmAmjyERtWES6EXTYqUPfJfQSzp2m",1000);
        let result_0 = await this.contract.createCircle(fee).send({
            feeLimit:10000,
            callValue:10000,
            shouldPollResponse:true
            })
        console.log(result_0)
        this.setState({result_0:JSON.stringify(result_0)})
    };

    getCircle = async () => {
        let result_0 = await this.contract.getCircle(1).call()
        console.log(result_0)
        this.setState({result_0:JSON.stringify(result_0)})
    };

    function_1 = async () => {
        let result_1 = await this.contract.getUserKey().call()
        console.log(result_1)
        this.setState({result_1:JSON.stringify(result_1)})
    };

    function_1 = async () => {
        let result_1 = await this.contract.getUserKey().call()
        console.log(result_1)
        this.setState({result_1:JSON.stringify(result_1)})
    };

    function_2 = async () => {
        let result_2 = await this.contract.getUserKey().call()
        console.log(result_2)
        this.setState({result_2:JSON.stringify(result_2)})
    };

    render() {
        return (
            <div className="App">
                <div>
                    <p>current address</p>
                    <p>{this.state.address}</p>
                    <hr></hr>
                </div>
                <div>
                    <p>current trx balance</p>
                    <p>{this.state.result_0}</p>
                    <button onClick={this.refreshBalance}>Refresh balance</button>
                    <hr></hr>
                </div>

                <div>
                    <p>function 1</p>
                    <p>{this.state.result_1}</p>
                    <button onClick={this.function_1}>test function 1</button>
                    <hr></hr>
                </div>
                <div>
                    <p>function 2</p>
                    <p>{this.state.result_2}</p>
                    <button onClick={this.function_2}>test function 2</button>
                    <hr></hr>
                </div>

            </div>
        );
    }
}

export default App;
