import React, {Component} from 'react';
import './App.css';
import * as artifact from './contracts/RedPacket'


class App extends Component {

    constructor(props) {
        super(props);

        window.tronWeb.setDefaultBlock('latest');

        this.contract = null;
        this.state = {
            address: null,
            balance: null,
            contract: null,
            tokenBalance: null,
            result_0: null,
            result_1: null,
            result_2: null,

        }
    }

    async componentDidMount() {

        let tronWeb = window.tronWeb;
        console.log('address', tronWeb.defaultAddress.base58)
        this.setState({address: tronWeb.defaultAddress.base58});
        let address = tronWeb.address.fromHex(artifact.networks['*'].address);
        console.log(artifact.abi, artifact.networks['*'].address, address)
        this.contract = tronWeb.contract(artifact.abi, address);
        console.log(this.contract);
    }

    //创建红包
    createPacket = async () => {
        let tron = 10*1000000; 
        let result_0 = await this.contract.createPacket(10, 2, "true","true","tron","hello").send({
            feeLimit:100000000,
            callValue:tron,
            shouldPollResponse:true
        })
        console.log(result_0)
        this.setState({createPacket:JSON.stringify(result_0)})
    };

    //抢红包
    getPacket = async () => {
        let result_0 = await this.contract.getPacket(1,"tron").send()
        console.log(result_0)
        this.setState({getPacket:JSON.stringify(result_0)})
    };

    //获取红包信息
    getPacketstruct = async () => {
        let result_0 = await this.contract.getPacketstruct(1).call()
        console.log(result_0)
        this.setState({getPacketstruct:JSON.stringify(result_0)})
    };

    //获取记录信息
    getRecord = async () => {
        let tron = 1000000
        //let result_0 = JSON.stringify(await this.contract.getRecord(1).call())
        let result_0 = await this.contract.getRecord(1).call()
        //let recorddata = result_0.substr(1,result_0.length-2).split(",");
        console.log(result_0)
        //this.setState({getRecordId:JSON.parse(JSON.stringify(recorddata[0]))._hex})
        //this.setState({getRecordOwner:recorddata[1]})
        //this.setState({getRecordTime:JSON.parse(JSON.stringify(recorddata[2]))._hex})
        //this.setState({getRecordMoney:JSON.parse(JSON.stringify(recorddata[3]))._hex})
        this.setState({getRecordId:JSON.parse(result_0[0])})
        this.setState({getRecordOwner:result_0[1]})
        this.setState({getRecordTime:JSON.parse(result_0[2])})
        this.setState({getRecordMoney:(JSON.parse(result_0[3]) / tron)})
    };


    render() {
        return (
            <div className="App">
                <div>
                    <p>current address</p>
                    <p>{this.state.address}</p>
                    <hr></hr>
                </div>

                <div>
                    <p>createPacket</p>
                    <p>{this.state.createPacket}</p>
                    <button onClick={this.createPacket}>createPacket</button>
                    <hr></hr>
                </div>

                <div>
                    <p>getPacket</p>
                    <p>{this.state.getPacket}</p>
                    <button onClick={this.getPacket}>getPacket</button>
                    <hr></hr>
                </div>

                <div>
                    <p>getPacketstruct</p>
                    <p>{this.state.getPacketstruct}</p>
                    <button onClick={this.getPacketstruct}>getPacketstruct</button>
                    <hr></hr>
                </div>

                <div>
                    <p>getRecord</p>
                    <p>{this.state.getRecordId}</p>
                    <p>{this.state.getRecordOwner}</p>
                    <p>{this.state.getRecordTime}</p>
                    <p>{this.state.getRecordMoney}</p>
                    <button onClick={this.getRecord}>getRecord</button>
                    <hr></hr>
                </div>


            </div>
        );
    }
}

export default App;
